import { useState } from "react";
import "./NewExpenseForm.css";

const NewExpenseForm = (props) => {

  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [visible, setVisible] = useState(false);

  const titleChangeHandler = (event) => {
    setTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
      setDate(event.target.value);
  }

  const expandExpenseForm = (event) => {
      setVisible(true);
  }

  const submitHandler = (event) => {
      event.preventDefault();

      const expenseData = {
          title: title,
          amount: amount,
          date: new Date(date)
      };

      props.onSaveExpenseData(expenseData);

      setTitle("");
      setAmount("");
      setDate("");
      setVisible(false)
  }

  return (
     (visible ? 
    (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} value={title}></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler} value={amount}></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2023-12-31" onChange={dateChangeHandler} value={date}></input>
        </div>
      </div>
      <div className="new-expense__actions">
        <button>Cancel</button>
        <button type="submit">Add Expense</button>
      </div>
    </form>
  )
  :
  (
    <div className="new-expense__controls">
        <button onClick={expandExpenseForm}>Add New Expense</button>
    </div>))
  );
};

export default NewExpenseForm;
