import { useState } from "react";
import "./NewExpenseForm.css";

const NewExpenseForm = () => {

  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");

  const titleChangeHandler = (event) => {
    setTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
      setDate(event.target.value);
  }

  const submitHandler = (event) => {
      event.preventDefault();

      const expenseData = {
          title: title,
          amount: amount,
          date: new Date(date)
      };

      console.log(expenseData)
  }

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} value={title}></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler} vlaue={amount}></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2023-12-31" onChange={dateChangeHandler} value={date}></input>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit" onSubmit={submitHandler}>Add Expense</button>
      </div>
    </form>
  );
};

export default NewExpenseForm;
